import { inject } from '@angular/core';
import { patchState, signalStore, withMethods, withState } from '@ngrx/signals';
import { rxMethod } from '@ngrx/signals/rxjs-interop';
import { catchError, of, pipe, switchMap, tap } from 'rxjs';

import type { MovieDetails, TvSeriesDetails } from '../models/movie.model';
import type { ErrorInfo } from '../services/error-handler.service';

import { ErrorHandlerService } from '../services/error-handler.service';
import { TmdbApiService } from '../services/tmdb-api.service';

interface DetailsState {
  error: ErrorInfo | null;
  isLoading: boolean;
  movieDetails: MovieDetails | null;
  tvDetails: null | TvSeriesDetails;
}

const initialState: DetailsState = {
  error: null,
  isLoading: false,
  movieDetails: null,
  tvDetails: null
};

export const DetailsStore = signalStore(
  { providedIn: 'root' },
  withState(initialState),
  withMethods((store, tmdbApi = inject(TmdbApiService), errorHandler = inject(ErrorHandlerService)) => ({
    loadMovieDetails: rxMethod<number>(
      pipe(
        tap(() => {
          patchState(store, {
            error: null,
            isLoading: true,
            movieDetails: null,
            tvDetails: null
          });
        }),
        switchMap((id) =>
          tmdbApi.getMovieDetails(id).pipe(
            catchError((error: unknown) => {
              const errorInfo = errorHandler.handleHttpError(error);
              errorHandler.logError(errorInfo);
              patchState(store, {
                error: errorInfo,
                isLoading: false
              });
              return of(null);
            })
          )
        ),
        tap((details) => {
          if (details) {
            patchState(store, {
              isLoading: false,
              movieDetails: details
            });
          }
        })
      )
    ),

    loadTvDetails: rxMethod<number>(
      pipe(
        tap(() => {
          patchState(store, {
            error: null,
            isLoading: true,
            movieDetails: null,
            tvDetails: null
          });
        }),
        switchMap((id) =>
          tmdbApi.getTvSeriesDetails(id).pipe(
            catchError((error: unknown) => {
              const errorInfo = errorHandler.handleHttpError(error);
              errorHandler.logError(errorInfo);
              patchState(store, {
                error: errorInfo,
                isLoading: false
              });
              return of(null);
            })
          )
        ),
        tap((details) => {
          if (details) {
            patchState(store, {
              isLoading: false,
              tvDetails: details
            });
          }
        })
      )
    ),
    reset: () => patchState(store, initialState)
  }))
);

import { HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
export interface ErrorInfo {
  code?: string;
  message: string;
  originalError?: unknown;
}

/**
 * Servicio para manejo de errores centralizado
 * Convierte errores técnicos en mensajes amigables para el usuario
 */
@Injectable({
  providedIn: 'root'
})
export class ErrorHandlerService {
  /**
   * Maneja errores HTTP y devuelve información de error amigable para el usuario
   * @param {unknown} error - El error a manejar
   * @returns {ErrorInfo} Información de error con mensaje amigable para el usuario
   */
  handleHttpError(error: unknown): ErrorInfo {
    if (error instanceof HttpErrorResponse) {
      switch (true) {
        case error.status === 0:
          return {
            code: 'NETWORK_ERROR',
            message: 'No se pudo conectar con el servidor. Por favor, verifica tu conexión a internet.',
            originalError: error
          };
        case error.status === 401:
          return {
            code: 'UNAUTHORIZED',
            message: 'API key inválida. Por favor, verifica tu configuración.',
            originalError: error
          };
        case error.status === 404:
          return {
            code: 'NOT_FOUND',
            message: 'No se encontró el recurso solicitado.',
            originalError: error
          };
        case error.status >= 500:
          return {
            code: 'SERVER_ERROR',
            message: 'Error del servidor. Por favor, intenta de nuevo más tarde.',
            originalError: error
          };
        default:
          return {
            code: `HTTP_${error.status}`,
            message: 'Ocurrió un error al procesar tu solicitud.',
            originalError: error
          };
      }
    }

    // Error del lado del cliente o desconocido
    return {
      code: 'UNKNOWN_ERROR',
      message: 'Ocurrió un error inesperado. Por favor, intenta de nuevo.',
      originalError: error
    };
  }
  /**
   * Registra el error en la consola (se puede extender para enviar al servicio de registro)
   * @param {ErrorInfo} error - Información de error para registrar
   */
  logError(error: ErrorInfo): void {
    console.error('Error occurred:', {
      code: error.code,
      message: error.message,
      originalError: error.originalError
    });
  }
}

