import { HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
export interface ErrorInfo {
  code?: string;
  message: string;
  originalError?: unknown;
}

/**
 * Servicio para manejo de errores centralizado
 * Convierte errores técnicos en mensajes amigables para el usuario
 */
@Injectable({
  providedIn: 'root'
})
export class ErrorHandlerService {
  /**
   * Maneja errores HTTP y devuelve información de error amigable para el usuario
   * @param {unknown} error - El error a manejar
   * @returns {ErrorInfo} Información de error con mensaje amigable para el usuario
   */
  handleHttpError(error: unknown): ErrorInfo {
    if (error instanceof HttpErrorResponse) {
      switch (true) {
        case error.status === 0:
          return {
            code: 'NETWORK_ERROR',
            message: 'No se pudo conectar con el servidor. Por favor, verifica tu conexión a internet.',
            originalError: error
          };
        case error.status === 401:
          return {
            code: 'UNAUTHORIZED',
            message: 'API key inválida. Por favor, verifica tu configuración.',
            originalError: error
          };
        case error.status === 404:
          return {
            code: 'NOT_FOUND',
            message: 'No se encontró el recurso solicitado.',
            originalError: error
          };
        case error.status >= 500:
          return {
            code: 'SERVER_ERROR',
            message: 'Error del servidor. Por favor, intenta de nuevo más tarde.',
            originalError: error
          };
        default:
          return {
            code: `HTTP_${error.status}`,
            message: 'Ocurrió un error al procesar tu solicitud.',
            originalError: error
          };
      }
    }

    // Error del lado del cliente o desconocido
    return {
      code: 'UNKNOWN_ERROR',
      message: 'Ocurrió un error inesperado. Por favor, intenta de nuevo.',
      originalError: error
    };
  }
  /**
   * Registra el error en la consola (se puede extender para enviar al servicio de registro)
   * @param {ErrorInfo} error - Información de error para registrar
   */
  logError(error: ErrorInfo): void {
    console.error('Error occurred:', {
      code: error.code,
      message: error.message,
      originalError: error.originalError
    });
  }
}

import { isPlatformBrowser } from '@angular/common';
import { inject, Injectable, PLATFORM_ID } from '@angular/core';

/**
 *
 */
@Injectable({ providedIn: 'root' })
export class LocalStorageService {
  private _platformId = inject(PLATFORM_ID);

  /**
   * Comprueba si se está ejecutando en el navegador
   * @returns {boolean} - True si se está ejecutando en el navegador, false en caso contrario
   */
  private isBrowser(): boolean {
    return isPlatformBrowser(this._platformId);
  }

  /**
   * Obtiene un valor de localStorage
   * @param {string} key - Clave del valor a obtener
   * @returns {null | string} - Valor obtenido de localStorage
   */
  get(key: string): null | string {
    if (!this.isBrowser()) return null;
    return localStorage.getItem(key);
  }
  /**
   * Elimina un valor de localStorage
   * @param {string} key - Clave del valor a eliminar
   */
  remove(key: string): void {
    if (!this.isBrowser()) return;
    localStorage.removeItem(key);
  }
  /**
   * Guarda un valor en localStorage
   * @param {string} key - Clave del valor a guardar
   * @param {string} value - Valor a guardar en localStorage
   */
  set(key: string, value: string): void {
    if (!this.isBrowser()) return;
    localStorage.setItem(key, value);
  }
}

import { DOCUMENT } from '@angular/common';
import { effect, inject, Injectable, signal } from '@angular/core';

import { LocalStorageService } from './localstorage.service';
import { WindowService } from './window.service';

/**
 * Servicio para gestionar el tema de la aplicación (modo claro/oscuro)
 * Persiste la preferencia de tema en localStorage y aplica la clase de tema al documento
 */
@Injectable({
  providedIn: 'root'
})
export class ThemeService {
  private readonly _document = inject(DOCUMENT);
  private readonly _localStorageService = inject(LocalStorageService);
  private readonly _windowService = inject(WindowService);

  private readonly _localStorageKey = 'theme-preference';

  /**
   * Señal para el estado del modo oscuro
   */
  readonly isDarkMode = signal<boolean>(this._getInitialTheme());

  constructor() {
    // Aplicar tema en inicialización y cuando cambie
    effect(() => {
      this._applyTheme(this.isDarkMode());
    });
  }

  /**
   * Aplica el tema añadiendo/eliminando la clase dark en el elemento document
   * @param {boolean} isDark - Verdadero para modo oscuro, falso para modo claro
   */
  private _applyTheme(isDark: boolean): void {
    const htmlElement = this._document.documentElement;
    if (isDark) {
      htmlElement.classList.add('dark');
    } else {
      htmlElement.classList.remove('dark');
    }
  }
  /**
   * Obtiene el tema inicial de localStorage o preferencia del sistema
   * @returns {boolean} Verdadero si se debe habilitar el modo oscuro
   */
  private _getInitialTheme(): boolean {
    // Comprobar localStorage primero
    const saved = this._localStorageService.get(this._localStorageKey);
    if (saved !== null) {
      return saved === 'dark';
    }

    // Recurrir a la preferencia del sistema
    return (
      (this._windowService?.nativeWindow?.matchMedia &&
        this._windowService.nativeWindow.matchMedia('(prefers-color-scheme: dark)').matches) ??
      false
    );
  }
  /**
   * Guarda la preferencia de tema en localStorage
   * @param {boolean} isDark - Verdadero para modo oscuro, falso para modo claro
   */
  private _saveThemePreference(isDark: boolean): void {
    this._localStorageService.set(this._localStorageKey, isDark ? 'dark' : 'light');
  }

  /**
   * Establece el tema explícitamente
   * @param {boolean} isDark - Verdadero para modo oscuro, falso para modo claro
   */
  setTheme(isDark: boolean): void {
    this.isDarkMode.set(isDark);
    this._saveThemePreference(isDark);
  }
  /**
   * Alterna entre modo claro y oscuro
   */
  toggleTheme(): void {
    this.isDarkMode.update((current) => !current);
    this._saveThemePreference(this.isDarkMode());
  }
}

import { HttpClient, HttpParams } from '@angular/common/http';
import { inject, Injectable } from '@angular/core';
import { map, Observable } from 'rxjs';

import type { PaginatedResponse } from '../models/api-response.model';
import type { Credits, MediaItem, MovieDetails, TvSeriesDetails } from '../models/movie.model';

import { environment } from '../../../environments/environment';

/**
 * Servicio para interactuar con la API de TMDB
 * Proporciona métodos para buscar películas, series de TV y funzionalità de búsqueda
 */
@Injectable({
  providedIn: 'root'
})
export class TmdbApiService {
  private readonly _http = inject(HttpClient);

  private readonly _apiBaseUrl = environment.apiBaseUrl;
  private readonly _apiKey = environment.apiKey;

  /**
   * Obtiene créditos de la película (elenco y equipo)
   * @param {number} id - ID de la película
   * @returns {Observable<Credits>} Observable de créditos
   */
  getMovieCredits(id: number) {
    const params = new HttpParams().set('api_key', this._apiKey);
    return this._http.get<Credits>(`${this._apiBaseUrl}/movie/${id}/credits`, { params });
  }
  /**
   * Obtiene información detallada para una película específica
   * @param {number} id - ID de la película
   * @returns {Observable<MovieDetails>} Observable de detalles de la película
   */
  getMovieDetails(id: number) {
    const params = new HttpParams().set('api_key', this._apiKey).set('append_to_response', 'credits');
    return this._http.get<MovieDetails>(`${this._apiBaseUrl}/movie/${id}`, { params });
  }
  /**
   * Obtiene películas filtradas por año
   * @param {number} year - Año de lanzamiento
   * @param {number} page - Número de página (por defecto 1)
   * @returns {Observable<PaginatedResponse<MediaItem>>} Observable de resultados de películas paginados
   */
  getMoviesByYear(year: number, page = 1) {
    const params = new HttpParams()
      .set('api_key', this._apiKey)
      .set('primary_release_year', year.toString())
      .set('sort_by', 'popularity.desc')
      .set('page', page.toString());
    return this._http.get<PaginatedResponse<MediaItem>>(`${this._apiBaseUrl}/discover/movie`, { params });
  }
  /**
   * Obtiene películas en tendencia para el día de hoy
   * @param {number} page - Número de página (por defecto 1)
   * @returns {Observable<PaginatedResponse<MediaItem>>} Observable de resultados de películas paginados
   */
  getTrendingMovies(page = 1) {
    const params = new HttpParams().set('api_key', this._apiKey).set('page', page.toString());
    return this._http.get<PaginatedResponse<MediaItem>>(`${this._apiBaseUrl}/trending/movie/day`, { params });
  }
  /**
   * Obtiene series de TV en tendencia para el día de hoy
   * @param {number} page - Número de página (por defecto 1)
   * @returns {Observable<PaginatedResponse<MediaItem>>} Observable de resultados de series de TV paginados
   */
  getTrendingTvSeries(page = 1) {
    const params = new HttpParams().set('api_key', this._apiKey).set('page', page.toString());
    return this._http.get<PaginatedResponse<MediaItem>>(`${this._apiBaseUrl}/trending/tv/day`, { params });
  }
  /**
   * Obtiene series de TV filtradas por año
   * @param {number} year - Año de primera emisión
   * @param {number} page - Número de página (por defecto 1)
   * @returns {Observable<PaginatedResponse<MediaItem>>} Observable de resultados de series de TV paginados
   */
  getTvSeriesByYear(year: number, page = 1) {
    const params = new HttpParams()
      .set('api_key', this._apiKey)
      .set('first_air_date_year', year.toString())
      .set('sort_by', 'popularity.desc')
      .set('page', page.toString());
    return this._http.get<PaginatedResponse<MediaItem>>(`${this._apiBaseUrl}/discover/tv`, { params });
  }
  /**
   * Obtiene créditos de la serie de TV (elenco y equipo)
   * @param {number} id - ID de la serie de TV
   * @returns {Observable<Credits>} Observable de créditos
   */
  getTvSeriesCredits(id: number) {
    const params = new HttpParams().set('api_key', this._apiKey);
    return this._http.get<Credits>(`${this._apiBaseUrl}/tv/${id}/credits`, { params });
  }
  /**
   * Obtiene información detallada para una serie de TV específica
   * @param {number} id - ID de la serie de TV
   * @returns {Observable<TvSeriesDetails>} Observable de detalles de la serie de TV
   */
  getTvSeriesDetails(id: number) {
    const params = new HttpParams().set('api_key', this._apiKey).set('append_to_response', 'credits');
    return this._http.get<TvSeriesDetails>(`${this._apiBaseUrl}/tv/${id}`, { params });
  }
  /**
   * Busca películas y series de TV
   * @param {string} query - Cadena de búsqueda
   * @returns {Observable<PaginatedResponse<MediaItem>>} Observable de resultados de búsqueda paginados
   */
  searchMulti(query: string) {
    const params = new HttpParams().set('api_key', this._apiKey).set('query', query).set('include_adult', 'false');
    return this._http.get<PaginatedResponse<MediaItem>>(`${this._apiBaseUrl}/search/multi`, { params }).pipe(
      map((response) => {
        response.results = response.results.filter((item) => item.media_type === 'movie' || item.media_type === 'tv');
        return response;
      })
    );
  }
}

import { inject, Injectable } from '@angular/core';

import { WINDOW } from '../tokens/window.token';

/**
 *
 */
@Injectable({ providedIn: 'root' })
export class WindowService {
  private readonly _windowRef = inject(WINDOW);

  /**
   * Obtiene la referencia nativa de window
   * @returns {null | Window} Referencia nativa de window
   */
  get nativeWindow(): null | Window {
    return this._windowRef;
  }

  /**
   * Añade un listener de eventos a la ventana
   * @param {K} type - Tipo de evento
   * @param {EventListenerOrEventListenerObject} listener - Listener de eventos
   * @param {AddEventListenerOptions | boolean} options - Opciones de listener
   */
  addEventListener<K extends keyof WindowEventMap>(
    type: K,
    listener: (this: Window, ev: WindowEventMap[K]) => unknown,
    options?: AddEventListenerOptions | boolean
  ) {
    if (!this._windowRef) return;
    this._windowRef.addEventListener(type, listener as EventListenerOrEventListenerObject, options);
  }
  /**
   * Obtiene el ancho interno de la ventana
   * @returns {null | number} Ancho interno de la ventana
   */
  getInnerWidth(): null | number {
    return this._windowRef?.innerWidth ?? null;
  }
  /**
   * Elimina un listener de eventos de la ventana
   * @param {K} type - Tipo de evento
   * @param {EventListenerOrEventListenerObject} listener - Listener de eventos
   * @param {boolean | EventListenerOptions} options - Opciones de listener
   */
  removeEventListener<K extends keyof WindowEventMap>(
    type: K,
    listener: (this: Window, ev: WindowEventMap[K]) => unknown,
    options?: boolean | EventListenerOptions
  ) {
    if (!this._windowRef) return;
    this._windowRef.removeEventListener(type, listener as EventListenerOrEventListenerObject, options);
  }
  /**
   * Desplaza la ventana hacia arriba
   */
  scrollToTop(): void {
    if (!this._windowRef) return;
    this._windowRef.scrollTo({ behavior: 'smooth', top: 0 });
  }
}

