            trendingTvSeries: response.tvSeries$.results,
            trendingTvSeriesPage: 1
          });
        })
      )
    ),

    /**
     * Maneja el cambio de pestaña de tendencias
     * @param {TrendingTab} tab - Valor de la pestaña seleccionada
     */
    onTrendingTabChange(tab: TrendingTab): void {
      patchState(store, { trendingTabSelected: tab });
    },

    /**
     * Actualiza todos los datos
     */
    refreshAll(): void {
      this.loadTrending();
      this.loadRecentReleases();
    }
  })),
  withHooks({
    // eslint-disable-next-line jsdoc/require-jsdoc
    onInit(store): void {
      store.loadTrending();
      store.loadRecentReleases();
    }
  })
)

src/app/core/store/search.store.ts.defaultValue:
{
  error: null,
  isLoading: false,
  query: '',
  results: []
}

signalStore(
  { providedIn: 'root' },
  withState(initialState),
  withMethods((store, tmdbApi = inject(TmdbApiService), errorHandler = inject(ErrorHandlerService)) => ({
    /**
     * Limpia los resultados y la consulta de búsqueda
     */
    clearSearch(): void {
      patchState(store, initialState);
    },

    /**
     * Realiza la búsqueda con debounce (300ms)
     * Limita los resultados a 5 elementos
     */
    search: rxMethod<string>(
      pipe(
        tap((query) => {
          patchState(store, { error: null, isLoading: true, query });
        }),
        switchMap((query) => {
          if (!query.trim()) {
            return of({ results: [] });
          }

          return tmdbApi.searchMulti(query).pipe(
            catchError((error: unknown) => {
              const errorInfo = errorHandler.handleHttpError(error);
              errorHandler.logError(errorInfo);
              patchState(store, { error: errorInfo, isLoading: false });
              return of({ results: [] });
            })
          );
        }),
        tap((response) => {
          // Limitar a 5 resultados
          const limitedResults = response.results.slice(0, 5);
          patchState(store, { isLoading: false, results: limitedResults });
        })
      )
    )
  }))
)

